INSERT INTO `comments` (`CommentID`, `UserID`, `BoardID`, `InstructorID`, `CommentContent`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(1, 13, 655, NULL, 'si sm쪽에서 일하는 개발자입니다. 8년정도 경력이있는대 서비스 기업으로이직을 고민중입니다. sism하고 서비스의 업무는 많이 다르다고 들었는데 이를 커버하기 위해서 대용량 트레픽같은 이슈를 해결했다는걸 사이드 프로젝트에 담고싶은데 어떻게하는것이 좋을까요', '2023-12-10 02:00:32', '2023-12-28 11:17:57', NULL),
	(2, 14, 24, NULL, '개발 블로그를 티스토리에 쓰고 계신데, 다른 대안도 많이 있을텐데 계속 쓰시는 이유가 있으실까요?', '2023-09-10 03:36:43', '2023-12-28 11:17:57', NULL),
	(3, 9, 52, NULL, '안녕하세요 취업을 준비하고 있는 취업준비생입니다 :D\r\n\r\n이번주 개발바닥 영상의 댓글에서도 동일한 질문을 하였는데 향로님이 직접 답변주시는 귀한 자리가 생겨서 여기에 한 번 더 질문을 드립니다!\r\n\r\n향로님과 호돌맨님께서 여러 영상을 통해 신입 개발자에게 강조하시는 역량이 어려운 추상화된 개념 (DDD, MSA, k8s 등등) 보다 언어 자체에 대한 심도있는 이해, SQL, 객체지향적 설계, 구현 능력 등이라고 저는 이해를 했는데요.\r\n\r\n신입 개발자로서 취업을 준비할 때 어느 정도의 역량을 준비해야할 지는 참 막막한 것 같습니다. 신입이라는 핑계로 \'이 정도까지만 해도 되겠지\' 라는 타협이라기보단 \'어느 것에 집중을 하는 것이 지금 내가 할 수 있는 올바른 방향일지\' 에 대한 고민이랄까요.\r\n\r\n향로님 및 호돌맨님께서 생각하시는 그 방향에 대해 다른 시니어 개발자 분들, 그리고 신입을 채용할 계획이 있는 회사의 개발자들 분들이 얼마나 공감할 지 향로님의 생각이 궁금합니다.\r\n\r\n한 가지 더 궁금한 걸 질문드리자면 신입 개발자가 어필할 수 있는 역량(?)에 대해서도 향로님의 생각을 말씀해주시면 감사하겠습니다 :D', '2023-11-28 12:20:12', '2023-12-28 11:17:57', NULL),
	(4, 1, 551, NULL, '안녕하세요. 저는 리걸테크 기업에서 프론트엔드 개발자로 활동중인 3년차 개발자 이우진 이라고 합니다.\r\n\r\n최근에 인공지능 대학원을 준비하고 있습니다. 결심하게 된 이유는 회사에서 자연어 처리 분야로 다중분류 예측 모델을 만들어 API로 개발해내면서 인공지능을 가지고 해결할 수 있는 문제가 정말 많다고 느꼈습니다. 코드로 해결할 수 없는 부분들을 인공지능으로 해결할 수 있으면서 동시에 프론트엔드도 할 줄 아는 개발자이면서 필요할 땐 인공지능을 직접 만들어 소프트웨어와 융합시킬 줄 아는 인재로 성장하고 동시에 이 기회에 석사 학위도 취득하고 싶어서 대학원을 준비하고 있습니다. 정말 고맙게도 회사에서는 대학원과 직장을 병행할 수 있는 근무 시간으로 조율해주겠다고 말씀도 주셔서 대학원도 안정적으로 다닐 수 있습니다.\r\n\r\n그런데 나중에 다른 회사에 지원할 기회가 생겨 지원했을 때 인사팀과 실무팀이 저의 이력서를 보고 이 사람은 프론트엔드를 잘하는건지, 인공지능을 잘하는건지 의아해 할 것 같고 얕은 지식으로 둘 다 한다라는 느낌을 줘서 오히려 감점이 될까봐 두렵기도 합니다.\r\n\r\n향로님께서 그런 이력서를 보셨을 때는 어떻게 생각하실 것 같나요? 제가 너무 걱정하는 걸까요?\r\n\r\n그리고... 향로님... 인프런에서 인프콘 추가 티켓 이벤트 계획 없으신가요?... 너무 가고싶습니다.................... 믿겨지지 않습니다..... 인프콘 행사를 못간다니.... 추가 이벤트.......... 없나요?....', '2023-12-13 04:15:50', '2023-12-28 11:17:57', NULL),
	(5, 11, 516, NULL, 'through: { attributes: [\'UserId\'] }\r\n\r\nas Likes 아래에 넣어주세요.\r\n\r\n그리고 질문 주제가 달라지면 새 질문에 달아주시면 좋겠습니다. 알림이 와도 찾기가 좀 힘듭니다.', '2023-04-23 09:14:46', '2023-12-28 11:17:57', NULL),
	(6, 19, 62, NULL, '이미지 한 장만 선택하시면 event.target.files가 아니라 event.target.file이 되어야 합니다. 그리고 input 태그에서도 multiple을 제거하셔야 하고요.\r\n\r\n하나를 올리는 데 반복문을 사용할 필요는 없겠죠?\r\n\r\nimage.append(\'profileImage\', event.target.file)하면 됩니다.', '2023-04-06 02:34:35', '2023-12-28 11:17:57', NULL),
	(7, 4, 673, NULL, '저도 동일한 문제로 계속해서 에러메시지를 보면서 검색도 해보고, 패키지를 다시 설치도 해보고 했습니다. 그러다 찾아서 해결했습니다. 제 사례를 공유해 드리겠습니다.\r\n\r\n선생님의 코드에 보면 mongoose 부분이 이렇게 되어 있었습니다. (깃허브에도 그렇게 되어 있었고요.)\r\n\r\n.connect(config.mongoURI, {\r\n\r\n그런데 dev.js에는 MONGO_URI 라고 되어 있지요. 그래서 아래와 같이 바꾸니 제대로 연결되네요.\r\n\r\n.connect(config.MONGO_URI, {\r\n\r\n공부하시는 분께 혹시 도움이 될지 몰라 늦었지만, 답변 달아봅니다. 결국 오타가 많은 부분에 영향을 줄 수 있다는 것을 다시 한번 느끼게 됩니다. 이 문제로 제가 며칠을 검색하고 머리아파 했거든요. \r\n\r\n열공 하세요... ', '2023-07-07 03:37:20', '2023-12-28 11:17:57', NULL),
	(8, 19, 263, NULL, '4장 6교시의 질문 문제였는데.. 4장 7교시에서\r\n\r\nyum의 패키지를 cd로 바꿔주고 따라해보니 설치가 되었습니다.', '2023-10-24 13:17:53', '2023-12-28 11:17:57', NULL),
	(9, 16, 240, NULL, '스프레드 연산자를 이용하면 같은 스테이트들은 그냥 그대로 두는게 아니었나요..?', '2023-09-17 18:28:32', '2023-12-28 11:17:57', NULL),
	(10, 20, 281, NULL, 'certbot이 작성해준게 아니라 직접 작성한겁니다. 애초부터 없었어요\r\n\r\n재시작도 해봤습니다.\r\n\r\n프론트 노드서버 말씀하신거면 3060으로 했습니다.\r\n\r\n재설치해봐도 여전히 server 설정은 작성 안해주네요...            ', '2023-05-28 10:36:15', '2023-12-28 11:17:57', NULL),
	(11, 12, 57, NULL, '1. saga나 reducer 작성하실 때 같은 것들끼리 선택하는 거는 어떻게 하는건가요?\r\n\r\n예를 들어 밑에 signUp을 밑으로 한 번 더 복사하고\r\n\r\ntype에 SIGN_UP 이부분들만 선택하는거요\r\n\r\nvscode에서는 alt + 방향키로 하면되는데 더 밑에거는 마우스 클릭해서 선택해야되고\r\n\r\nctrl+f로 찾아서 alt +엔터 누르면 모든 게 선택되어버려서 어떤 기능인지 궁금합니다!           ', '2023-03-31 09:00:24', '2023-12-28 11:17:57', NULL),
	(12, 2, 278, NULL, '1. sequelize sync + nodemon 항목을 보던 중, npx sequelize db:create 이후에 node app을 하니 에러가 떴습니다. 각 model파일마다 아래 코드를 넣어줬는데도 불구하고 에러가 뜨는 이유가 무엇인가요? 해결하려면 어떻게 해야되나요?       ', '2023-11-11 11:33:57', '2023-12-28 11:17:57', NULL),
	(13, 18, 392, NULL, '자세히보니 리듀서가 잘못되어있었네요ㅠ 덕분에 잘 해결되었습니다\r\n\r\n1. nodejs 강의 구매하려고 하는데요, 유튜브보니 노드교과서 개정판이 있던데 이것과 현재 인프런에 업로드 되어있는 강의와는 다른건가요?\r\n\r\n2. 다른거라면 개정된 걸 구매하고 싶은데 업로드 예정일은 언제인가요?   ', '2023-05-23 13:51:49', '2023-12-28 11:17:57', NULL),
