INSERT INTO `reviews` (`ReviewID`, `EnrollmentID`, `ReviewComment`, `ReviewRating`, `created_at`, `updated_at`, `deleted_at`) VALUES
	(1, 3, '와....대박이에요. 파트 10까지 나와도 되고, 파트당 20만원씩 책정하셔도 다 구매할테니 제발 내용 덜어내거나 난이도 조절하지 마시고 자잘한 부분도 포기하지 말아주세요 ㅜㅠ \r\n얼마전에 수강생들에게 단체메일 보내셨던데 내용 굳이 안 덜어내셔도 깊이가 애매해지거나 하지않으니 걱정마시구 (보는 사람이 건너뛰면 되는 문제기도 하지만 정 신경쓰이시면 파트7 이후 외전으로 다루셔도 괜찮습니다..!) \r\n어디에도 이런 강의 없습니다. 저는 이만 팬티 갈아입으러 갑니다..', 1.0, '2023-01-12 01:54:06', '2023-12-29 05:56:22', NULL),
	(2, 238, '정말 좋네요, Rookiss 님 때문에 사교육을 굳이 받지 않더라도 두꺼운 전공 서적을 사서 혼자 끙끙 앓으면서 고통 받지 않아도 취업 준비를 할 수 있을 것 같습니다. \r\n두꺼운 책을 혼자 연구 하는 것도 의미가 있겠지만 그것을 연구한 분이 알기 쉽게 설명을 해주고 중요한 부분만 예제들을 써주시면서 설명을 자세하게 하니 두꺼운 책을 사서 보지도 않을거 선생님의 강의로 대체를 할 수 있는것 같습니다.', 3.0, '2023-09-29 03:36:28', '2023-12-29 05:56:22', NULL),
	(3, 101, '학부 과정에서 만든 게임 경험으로 너무 부족한 제 실력을 깨닫고 더 배우기 위해 찾아들었던 이 강의가 정말 계속 더 성장할 수 있다는 것을 깨닫게 해주었습니다.\r\n이번에 \'Rookiss\'님과 이력에 있는 회사에 취직했고, 곧 입사 예정입니다.\r\n감사 인사드리고 싶어 이렇게 수강평 남깁니다.\r\n정말로 감사드립니다!\r\n나중에 저도 열심히 잘해서 제 지식을 공유할 수 있는 사람이 돼보겠습니다.', 4.0, '2023-01-31 06:07:39', '2023-12-29 05:56:22', NULL),
	(4, 22, '수강들으면서 제 디자인의 꿈을 키우고있어여, 개발과정에만 집중하다 이런 강의도 배우니, 커리어가 상승하는 느낌과 동시에 디자인적인 재미까지 더해가는 것같아 즐겁네요!', 5.0, '2023-08-17 00:43:19', '2023-12-29 05:56:22', NULL),
	(5, 246, '잘 정리된 내용을 잘 정리해서 실습과 함께 알려주시니 너무 좋습니다!', 3.0, '2023-07-23 20:01:56', '2023-12-29 05:56:22', NULL),
	(6, 38, '따라하기 좋아요, 다만 기능에 관한 설명이 조금 들어갔으면 좋겠습니다. \r\n예를들면 VScode 확장 부분도 이야기해주시고 또 단축키나 이런부분도 들어갔으면 너무좋았을 것 같습니다', 5.0, '2023-08-31 13:16:47', '2023-12-29 05:56:22', NULL),
	(7, 134, '와 진짜 대박이에요...... 다른 강의 듣다가 포기했었는데 재미있게 들었습니다.\r\nn회독해서 더욱 완벽해지겠습니다.', 4.0, '2023-12-19 09:07:35', '2023-12-29 05:56:22', NULL),
